// Generate content data
const domains = ['hing.me', 'playu.co'];
const financeKeywords = [
  'mortgage rates 2024',
  'investment strategies',
  'personal finance tips',
  'retirement planning',
  'debt management'
];
const gamingKeywords = [
  'pc gaming setup',
  'esports strategies',
  'gaming laptop guide',
  'streaming setup',
  'game optimization'
];

const randomDomain = domains[Math.floor(Math.random() * domains.length)];
const keywords = randomDomain === 'hing.me' ? financeKeywords : gamingKeywords;
const keyword = keywords[Math.floor(Math.random() * keywords.length)];

const title = `${keyword.charAt(0).toUpperCase() + keyword.slice(1)} - Complete Guide 2024`;
const body = `This comprehensive guide covers everything you need to know about ${keyword}. Learn expert tips, strategies, and best practices to achieve your goals.\n\n## Key Benefits\n\n- Professional insights and analysis\n- Step-by-step implementation guide\n- Real-world examples and case studies\n- Expert recommendations\n\n## Getting Started\n\nFollow our detailed guide to master ${keyword} and achieve exceptional results in your journey.`;

return [{
  json: {
    keyword: keyword,
    domain: randomDomain,
    title: title,
    body: body,
    timestamp: new Date().toISOString()
  }
}];
